import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const getRandomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const App = () => {
  const [color, setColor] = useState('');
  const [options, setOptions] = useState([]);
  const [result, setResult] = useState('');
  const [revealed, setRevealed] = useState({});
  const [score, setScore] = useState(0);
  const [questionCount, setQuestionCount] = useState(10);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [isStarted, setIsStarted] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const timerRef = useRef(null);

  useEffect(() => {
    if (isStarted) {
      setNewGame();
      setStartTime(new Date());
      timerRef.current = setInterval(() => {
        const elapsed = (new Date() - startTime) / 1000;
        if (elapsed >= 30) {
          clearInterval(timerRef.current);
          setResult('時間切れ!');
          setIsStarted(false);
        }
      }, 1000);
    }
  }, [isStarted]);

  const setNewGame = () => {
    const correctColor = getRandomColor();
    const options = [correctColor, ...Array(5).fill().map(getRandomColor)].sort(() => Math.random() - 0.5);
    setColor(correctColor);
    setOptions(options);
    setRevealed({});
    setStartTime(new Date());
  };

  const handleOptionClick = (option) => {
    const elapsed = (new Date() - startTime) / 1000;
    if (option === color) {
      const points = elapsed <= 10 ? 200 : 150;
      setScore((prevScore) => prevScore + points);
      setResult('正解!');
      setCurrentQuestion((prevQuestion) => prevQuestion + 1);
      if (currentQuestion + 1 < questionCount) {
        setTimeout(setNewGame, 1000);
      } else {
        clearInterval(timerRef.current);
        setResult('ゲーム終了!');
        setIsStarted(false);
      }
    } else {
      setScore((prevScore) => prevScore - 25);
      setResult(`違うよ!`);
      setRevealed((prevRevealed) => ({ ...prevRevealed, [option]: true }));
      setTimeout(() => {
        setOptions((prevOptions) => prevOptions.filter((opt) => opt !== option));
      }, 500);
    }
  };

  const handleStart = () => {
    setScore(0);
    setCurrentQuestion(0);
    setIsStarted(true);
    setNewGame();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-200">
      {!isStarted ? (
        <div className="flex flex-col items-center">
          <h1 className="text-2xl mb-5">カラーコード当てゲーム</h1>
          <div className="flex mb-5">
            <button onClick={() => setQuestionCount(10)} className="p-2 m-2 text-lg rounded bg-blue-500 text-white cursor-pointer">
              10問
            </button>
            <button onClick={() => setQuestionCount(20)} className="p-2 m-2 text-lg rounded bg-blue-500 text-white cursor-pointer">
              20問
            </button>
            <input
              type="number"
              placeholder="カスタム"
              className="p-2 m-2 text-lg rounded border"
              onChange={(e) => setQuestionCount(parseInt(e.target.value) || 10)}
            />
          </div>
          <button onClick={handleStart} className="p-2 text-lg rounded bg-green-500 text-white cursor-pointer">
            スタート
          </button>
        </div>
      ) : (
        <div className="flex flex-col items-center">
          <div id="colorCircle" className="w-24 h-24 rounded-full mb-5" style={{ backgroundColor: color }}></div>
          <div className="flex flex-wrap justify-center">
            {options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleOptionClick(option)}
                className={`p-2 m-2 text-lg rounded cursor-pointer transition-all duration-500 ${revealed[option] ? 'fade-out' : ''
                  }`}
                style={{ backgroundColor: revealed[option] ? option : '#FFFFFF', color: '#000000' }}
              >
                {option}
              </button>
            ))}
          </div>
          <p id="result" className="text-lg mt-5">{result}</p>
          <p className="text-lg mt-2">スコア: {score}</p>
        </div>
      )}
    </div>
  );
};

export default App;
